import axios from 'axios';
import { 
    GET_ITEMS, 
    ADD_ITEMS, 
    DELETE_ITEMS, 
    ITEMS_LOADING 
} from '../actions/types';

const URL = 'http://localhost:4000/items'

export const getItems = () => dispatch => {
    dispatch(setItemsLoading());
    axios.get(`${URL}`)
    .then(res=> dispatch({
        type:GET_ITEMS,
        payload: res.data
    }))
};

export const addItem = item  => dispatch=> {
    axios.post(`${URL}/create`,item)
    .then(res=> dispatch({
        type: ADD_ITEMS,
        payload: res.data
    }))
};

export const deleteItem = id => {
    return {
        type: DELETE_ITEMS,
        payload: id
    };
};



export const setItemsLoading = () => {
    return {
        type: ITEMS_LOADING
    };
};
