import React, { Component } from 'react'
import { Container, ListGroup, ListGroupItem, Button } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { connect } from 'react-redux';
import { getItems, deleteItem,updateItemDone } from '../actions/itemActions';
import PropTypes from 'prop-types';

class ShoppingList extends Component {

  componentDidMount() {
    this.props.getItems();
  }

  onDeleteClick = (id) => {
    console.log(id)
    this.props.deleteItem(id);
  }

  render() {
    const { items } = this.props.item;
    console.log(this.props.item);
    return (
      <Container>
        <ListGroup>
          <TransitionGroup className="shopping-list">
            {items.map((item, index) => (
              <CSSTransition key={index} timeout={500} classNames="fade">
                <ListGroupItem>
                  <Button
                    className="remove-btn"
                    color="danger"
                    size="sm"
                    onClick={this.props.deleteItem(this, item._id)}
                  > &times;
                  </Button>
                  <Button
                    className="removse-btn"
                    color="info"
                    size="sm"
                    onClick={this.onUpdate.bind(this, item._id)}
                  > !
                  </Button>
                  <p>
                    ID: {item._id}
                  </p>
                  <p>
                    Title: {item.title}
                  </p>
                  <p>
                    Description: {item.description}
                  </p>
                  <p>
                    Date: {item.date}
                  </p>
                  {(item.taskStatus)? (<Button onClick={() => this.props.setTaskNotDone(item._id)}>Pending</Button>)
                  : (<Button onClick={() => this.props.setTaskDone(item)}>Done</Button>)
                }
                </ListGroupItem>
              </CSSTransition>
            ))}
          </TransitionGroup>
        </ListGroup>

      </Container>
    )
  }
}

ShoppingList.propTypes = {
  getItems: PropTypes.func.isRequired,
  deleteItem: PropTypes.func.isRequired,
  updateItemDone: PropTypes.func.isRequired,
  item: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
  item: state.item
})

export default connect(
  mapStateToProps,
  { getItems, deleteItem, updateItemDone }
)(ShoppingList)
